using CompetitiveProgramming.Metadata;
using System;
using System.IO;

namespace CompetitiveProgramming.AdventOfCode._2015;

[AlgorithmInfo(
    url: "https://adventofcode.com/2015/day/1",
    timeComplexity: ComplexityValues.Linear,
    memoryComplexity: ComplexityValues.Constant,
    new[] {
        AlgorithmTags.Adhoc
    }
)]
class P01
{
    static StreamWriter output = new StreamWriter(Console.OpenStandardOutput());

    internal static void Run()
    {
        var input = Console.ReadLine();
        var result = Solve(input);
        output.WriteLine($"Floor: {result.Floor}");
        output.WriteLine($"Basement Index: {result.BasementIndex}");
        output.Flush();
    }

    static (int Floor, int BasementIndex) Solve(string input)
    {
        int basement = 0;
        int result = 0;
        for (int i = 0; i < input.Length; i++)
        {
            char c = input[i];
            if (c == '(')
                result++;
            else
                result--;

            if (basement == 0 && result == -1)
            {
                basement = i + 1;
            }
        }

        return (Floor: result, BasementIndex: basement);
    }

